html,
html[data-theme='light-theme'] {
  .home-main-bgimage {
    background: url(./../images/main-background-images/pattern-home-light.jpg);
    transition: all 0.5s $animation-function;
  }

  .library-main-bgimage {
    background: url(./../images/main-background-images/pattern-library-light.jpg);
    transition: all 0.5s $animation-function;
    flex-grow: 1;
  }
}

html[data-theme='dark-theme'] {
  .home-main-bgimage {
    background: url(./../images/main-background-images/pattern-home-dark.jpg);
    transition: all 0.5s $animation-function;
  }

  .library-main-bgimage {
    background: url(./../images/main-background-images/pattern-library-dark.jpg);
    transition: all 0.5s $animation-function;
    flex-grow: 1;
  }

  .one-card__title,
  .movie__content > *,
  .properties-list .property-item,
  .movie__btn,
  .modal__close,
  .footer,
  .footer__link,
  .card__text,
  .card__name,
  .tui-page-btn,
  .movie__button-trailer,
  .movie__about-header,
  .card__icon,
  .list-is-empty__text,
  .s-title,
  .similar__movie-title,
  .value-item {
    color: #fff;
  }

  .card__icon:hover,
  .modal__close:hover {
    color: $accent-color;
  }

  .properties-values .value-item,
  .movie__about {
    color: #d8d2d2;
  }

  .tui-pagination .tui-ico-first,
  .tui-pagination .tui-ico-prev,
  .tui-pagination .tui-ico-next,
  .tui-pagination .tui-ico-last,
  .tui-pagination .tui-ico-ellip {
    filter: brightness(6);
  }

  .tui-is-disabled {
    filter: brightness(0.5);
  }

  .movie__btn,
  .movie__button-trailer {
    border-color: #fff;
  }

  .modal-window,
  .footer,
  .modal-trailer {
    background-color: #272727;
  }
}

.theme-switcher {
  position: absolute;
  top: 100px;
  right: 213px;
}

.theme-switch__input,
.theme-switch__label {
  position: absolute;
  z-index: 1;
}

.theme-switch__input {
  opacity: 0;

  &:hover,
  &:focus {
    + .theme-switch__label {
      background-color: #e8cfc061;
    }

    + .theme-switch__label span::after {
      background-color: lighten(#ffffff61, 10%);
    }
  }
}

.theme-switch__label {
  padding: 13px;
  margin: 60px;
  transition: background-color 0.5s ease-in-out;
  width: 55px;
  height: 3px;
  border-radius: 50px;
  text-align: center;
  background-color: #e8cfc061;
  box-shadow: -4px 4px 15px inset rgba(0, 0, 0, 0.4);

  &::before,
  &::after {
    font-size: 1.5rem;
    position: absolute;
    transform: translate3d(0, -50%, 0);
    top: 50%;
  }

  &::before {
    content: '\263C';
    right: 100%;
    margin-right: 10px;
    color: #ff6b08;
  }

  &::after {
    content: '\263E';
    left: 100%;
    margin-left: 10px;
    color: lightSlateGray;
  }

  .circle {
    position: absolute;
    bottom: calc(100% + 10px);
    left: 30px;
    width: 100%;
  }

  .circle::after {
    position: absolute;
    top: calc(100% + 15px);
    left: -25px;
    width: 16px;
    height: 16px;
    content: '';
    border-radius: 50%;
    background-color: #fff;
    transition: transform 0.5s, background-color 0.5s;
    box-shadow: -3px 3px 8px rgba(0, 0, 0, 0.4);
  }
}

.theme-switch__input:checked ~ .theme-switch__label {
  background-color: lightSlateGray;

  &::before {
    color: lightSlateGray;
  }

  &::after {
    color: #fff;
  }

  .circle::after {
    transform: translate3d(30px, 0, 0);
  }
}

