sources.sass/layouts/_pagination.scss:
.tui-pagination .tui-ico-first,
.tui-pagination .tui-ico-prev,
.tui-pagination .tui-ico-next,
.tui-pagination .tui-ico-last,
.tui-pagination .tui-ico-ellip {
  background: url(data:image/png;base64,)
    no-repeat 0 0;
  display: inline-block;
  vertical-align: middle;
  font-size: 0px;
}

.tui-pagination .tui-ico-first {
  width: 11px;
  height: 10px;
  background-position: 0px 0px;
}

.tui-pagination .tui-ico-prev {
  width: 6px;
  height: 10px;
  background-position: -26px 0px;
}

.tui-pagination .tui-ico-next {
  width: 6px;
  height: 10px;
  background-position: -13px -24px;
}

.tui-pagination .tui-ico-last {
  width: 11px;
  height: 10px;
  background-position: 0px -12px;
}

.tui-pagination .tui-ico-ellip {
  width: 10px;
  height: 10px;
  margin-top: 16px;
  vertical-align: top;
  background-position: 0px -24px;
}

.tui-pagination .tui-is-disabled:hover {
  background: #fff;
  cursor: default;
}

.tui-pagination .tui-is-disabled .tui-ico-prev {
  background-position: -26px -12px;
}

.tui-pagination .tui-is-disabled .tui-ico-first {
  background-position: -13px 0px;
}

.tui-pagination .tui-is-disabled .tui-ico-next {
  background-position: -21px -24px;
}

.tui-pagination .tui-is-disabled .tui-ico-last {
  background-position: -13px -12px;
}
.tui-pagination {
  height: 40px;
  width: auto;
  margin-left: auto;
  margin-right: auto;
  padding-bottom: 60px;
  display: flex;
  justify-content: center;
  align-items: center;
}

.tui-prev,
.tui-next:hover {
  background-color: #ff6b08;
}

.tui-prev-is-ellip,
.tui-next-is-ellip {
  padding-bottom: 5px;
  background-color: $footer-background-color;
}

.tui-page-btn:hover {
  cursor: pointer;
  background-color: #ff6b08;
  color: $accent-text-color;
}

.pagination.container {
  margin-top: 40px;
  margin-bottom: 40px;
}

.tui-page-btn {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 40px;
  height: 40px;
  margin: 0;
  border-radius: 5px;
  color: $main-text-color;
  font-weight: 500;
  border: none;
  outline: none;
  cursor: pointer;
  background-color: #ffffff00;

  text-decoration: none;
}

.tui-prev,
.tui-next,
.tui-first,
.tui-last {
  width: 40px;
  height: 100%;
  border-radius: 5px;
  border: none;
  color: black;
  background-color: $footer-background-color;
  border-radius: 5px;
}

.tui-prev-is-ellip,
.tui-next-is-ellip {
  &:hover {
    background-color: #ff6b08;
    cursor: default;
    color: $accent-text-color;
  }
}

.tui-pagination .tui-is-selected,
.tui-pagination strong {
