  .slider__navigation {
    top: 50%;
  }
}
.slider__navigation:hover, .slider__navigation:focus {
  color: #ff6b01;
}

.slider__navigation--left {
  left: 0;
}

.slider__navigation--right {
  right: 0;
}

.slider__icon {
  fill: currentColor;
  stroke: white;
}

.slider__dots {
  text-align: center;
  cursor: pointer;
}

.slider__dot {
  display: inline-block;
  padding: 8px;
  background-color: #8c8c8c;
  border-radius: 50%;
}
.slider__dot:not(:last-child) {
  margin-right: 10px;
}
@media screen and (min-width: 768px) {
  .slider__dot {
    width: 40px;
    height: 40px;
    padding: 0;
    overflow: hidden;
  }
}

.slider__dot-photo {
  display: none;
}
@media screen and (min-width: 768px) {
  .slider__dot-photo {
    display: block;
    width: 100%;
    height: 100%;
  }
}

.scroll-to-top {
  visibility: visible;
  opacity: 0;
  transition: opacity 0.5s cubic-bezier(0.4, 0, 0.2, 1);
}

.show {
  visibility: visible;
  opacity: 1;
}

.arrow-up {
  height: 35px;
  width: 35px;
  border: 1px solid #ff6b01;
  border-radius: 50%;
  position: fixed;
  bottom: 20px;
  right: 60px;
  cursor: pointer;
  transition: all 0.5s cubic-bezier(0.25, 1.7, 0.35, 1.5);
  overflow: hidden;
}
@media screen and (max-width: 768px) {
  .arrow-up {
    bottom: 20px;
    right: 10px;
  }
}
@media screen and (max-width: 480px) {
  .arrow-up {
    bottom: 20px;
    right: 15px;
  }
}

.arrow-slide {
  left: 0;
  top: -100%;
  width: 100%;
  height: 100%;
  background: #ff6b01c0;
  position: absolute;
  display: block;
  z-index: 0;
}

.left-arm {
  position: absolute;
  z-index: 1;
  background-color: transparent;
  top: 13px;
  left: 5px;
  width: 20px;
  display: block;
  transform: rotate(-45deg);
}
.left-arm:after {
  content: "";
  background-color: #ff6b01;
  width: 13px;
  height: 1px;
  display: block;
  border-radius: 1px;
  transition: all 0.5s cubic-bezier(0.25, 1.7, 0.35, 1.5);
  transform-origin: right center;
  z-index: -1;
}

.right-arm {
  position: absolute;
  z-index: 1;
  background-color: transparent;
  top: 18px;
  left: 14px;
  width: 20px;
  display: block;
  transform: rotate(45deg);
  border-radius: 2px;
}
.right-arm:after {
  content: "";
  background-color: #ff6b01;
  width: 13px;
  height: 1px;
  display: block;
  border-radius: 1px;
  transition: all 0.5s cubic-bezier(0.25, 1.7, 0.35, 1.5);
  transform-origin: left center;
  z-index: -1;
}

.arrow-up:hover {
  transition: all 0.1s;
}
.arrow-up:hover .left-arm:after {
  transform: rotate(-10deg);
}
.arrow-up:hover .right-arm:after {
  transform: rotate(10deg);
}
.arrow-up:hover .arrow-slide {
  transition: all 0.4s ease-in-out;
  transform: translateY(200%);
}

.button_clear-library {
  border-radius: 50%;
  height: 35px;
  width: 35px;
  border: 1px solid #ff6b01;
  background-color: transparent;
  display: flex;
  text-align: center;
  justify-content: center;
  position: fixed;
  bottom: 90px;
  right: 60px;
  cursor: pointer;
  overflow: hidden;
}
@media screen and (max-width: 768px) {
