.theme-switcher {
  position: absolute;
  top: 100px;
  right: 213px;
}

.theme-switch__input,
.theme-switch__label {
  position: absolute;
  z-index: 1;
}

.theme-switch__input {
  opacity: 0;
}
.theme-switch__input:hover + .theme-switch__label, .theme-switch__input:focus + .theme-switch__label {
  background-color: #e8cfc061;
}
.theme-switch__input:hover + .theme-switch__label span::after, .theme-switch__input:focus + .theme-switch__label span::after {
  background-color: rgba(255, 255, 255, 0.3803921569);
}

.theme-switch__label {
  padding: 13px;
  margin: 60px;
  transition: background-color 0.5s ease-in-out;
  width: 55px;
  height: 3px;
  border-radius: 50px;
  text-align: center;
  background-color: #e8cfc061;
  box-shadow: -4px 4px 15px inset rgba(0, 0, 0, 0.4);
}
.theme-switch__label::before, .theme-switch__label::after {
  font-size: 1.5rem;
  position: absolute;
  transform: translate3d(0, -50%, 0);
  top: 50%;
}
.theme-switch__label::before {
  content: "☼";
  right: 100%;
  margin-right: 10px;
  color: #ff6b08;
}
.theme-switch__label::after {
  content: "☾";
  left: 100%;
  margin-left: 10px;
  color: lightSlateGray;
}
.theme-switch__label .circle {
  position: absolute;
  bottom: calc(100% + 10px);
  left: 30px;
  width: 100%;
}
.theme-switch__label .circle::after {
  position: absolute;
  top: calc(100% + 15px);
  left: -25px;
  width: 16px;
  height: 16px;
  content: "";
  border-radius: 50%;
  background-color: #fff;
  transition: transform 0.5s, background-color 0.5s;
  box-shadow: -3px 3px 8px rgba(0, 0, 0, 0.4);
}

.theme-switch__input:checked ~ .theme-switch__label {
  background-color: lightSlateGray;
}
.theme-switch__input:checked ~ .theme-switch__label::before {
  color: lightSlateGray;
}
.theme-switch__input:checked ~ .theme-switch__label::after {
  color: #fff;
}
.theme-switch__input:checked ~ .theme-switch__label .circle::after {
  transform: translate3d(30px, 0, 0);
}

