  }

  @media screen and (max-width: $mobile-width) {
    bottom: 110px;
    right: 15px;
  }

  &.hidden {
    display: none;
  }

  &:hover,
  &:focus {
    cursor: pointer;
  }
}

.button_clear-library:hover {
  .arrow-slide {
    transition: all 0.4s ease-in-out;
    transform: translateY(200%);
  }
}

.delete-icon {
  width: 28px;
  height: 20px;
  fill: $accent-color;
  margin: auto;
}

/* Tooltip text */
.tooltip {
  overflow: visible;
  width: 35px;
  height: 35px;
  position: fixed;
  bottom: 110px;
  right: 60px;

  &.hidden {
    display: none;
  }

  @media screen and (max-width: $tablet-width) {
    bottom: 110px;
    right: 10px;
  }

  @media screen and (max-width: $mobile-width) {
    bottom: 110px;
    right: 15px;
  }

}

.tooltiptext {
  visibility: hidden;
  width: 120px;
  color: $accent-color;
  text-align: center;

  /* Position the tooltip text */
  position: absolute;
  bottom: 25px;
  right: 5px;

  @media screen and (max-width: $tablet-width) {
    bottom: 40px;
    right: 10px;
  }
}

.tooltip:hover .tooltiptext {
  visibility: visible;
}

